import { useState } from "react";

const Prices = (props) => {
  const [currency, setCurrency] = useState("USD");

  const renderPrices = () => {
    switch (currency) {
      case "USD":
        return (
          <ul className="list-group">
            <li className="list-group-item">
              Bitcoin Price in {props.data.USD.code} rate is{" "}
              {props.data.USD.symbol}
              {props.data.USD.rate}
            </li>
          </ul>
        );
      case "EUR":
        return (
          <ul className="list-group">
            <li className="list-group-item">
              Bitcoin Price in {props.data.EUR.code} rate is{" "}
              {props.data.EUR.symbol}
              {props.data.EUR.rate}
            </li>
          </ul>
        );
      case "GBP":
        return (
          <ul className="list-group">
            <li className="list-group-item">
              Bitcoin Price in {props.data.GBP.code} rate is{" "}
              {props.data.GBP.symbol}
              {props.data.GBP.rate}
            </li>
          </ul>
        );
    }
  };

  return <div>{renderPrices()}</div>;
};

export default Prices;
