import { useState } from "react";

const Prices = (props) => {
  const [currency, setCurrency] = useState("USD");

  const renderList = () => {
    switch (currency) {
      case "USD":
        return <li>USD</li>;
      case "GBP":
        return <li>GBP</li>;
      case "EUR":
        return <li>EUT</li>;
      default:
        return <li>USD</li>;
    }
  };

  return <div>{renderList}</div>;
};

export default Prices;
