import { useState } from "react";

const Prices = (props) => {
  const [currency, setCurrency] = useState("USD");
  console.log(props);

  const renderPrices = () => {
    switch (currency) {
      case "USD":
        return (
          <ul className="list-group">
            <li className="list-group-item">
              Bitcoin Price in {props.bpi.bpi.USD.code} rate is{" "}
              {props.bpi.bpi.USD.symbol}
              {props.bpi.bpi.USD.rate}
            </li>
          </ul>
        );
      case "EUR":
        return (
          <ul className="list-group">
            <li className="list-group-item">
              Bitcoin Price in {props.bpi.bpi.EUR.code} rate is{" "}
              {props.bpi.bpi.EUR.symbol}
              {props.bpi.bpi.EUR.rate}
            </li>
          </ul>
        );
      case "GBP":
        return (
          <ul className="list-group">
            <li className="list-group-item">
              Bitcoin Price in {props.bpi.bpi.GBP.code} rate is{" "}
              {props.bpi.bpi.GBP.symbol}
              {props.bpi.bpi.GBP.rate}
            </li>
          </ul>
        );
    }
  };

  return <div>{renderPrices()}</div>;
};

export default Prices;
