import { useState } from "react";

const Prices = (props) => {
  const [currency, setCurrency] = useState("USD");

  const renderPrices = () => {
    switch (currency) {
      case "USD":
        return (
          <ul className="list-group">
            <li className="list-group-item">
              Bitcoin Price in {props.data.bpi.USD.code} rate is{" "}
              {props.data.bpi.USD.symbol}
              {props.data.bpi.USD.rate}
            </li>
          </ul>
        );
      default:
        return <div>Buy Guys</div>;
    }
  };

  return <div>{renderPrices()}</div>;
};

export default Prices;
