import { useState } from "react";
import styles from "./Prices.module.scss";

const Prices = (props) => {
  const [currency, setCurrency] = useState("USD");
  console.log(props);

  const renderPrices = () => {
    switch (currency) {
      case "USD":
        return (
          <ul className="list-group">
            <li className="list-group-item">
              Bitcoin rate for{" "}
              <span className="badge badge-primary">
                {props.bpi.bpi.USD.description}
              </span>{" "}
              is:&nbsp;
              <span className="badge badge-warning">
                {props.bpi.bpi.USD.rate}
              </span>
            </li>
          </ul>
        );
      case "EUR":
        return (
          <ul className="list-group">
            <li className="list-group-item">
              Bitcoin rate for{" "}
              <span className="badge badge-primary">
                {props.bpi.bpi.EUR.description}
              </span>{" "}
              is:&nbsp;
              <span className="badge badge-warning">
                {props.bpi.bpi.EUR.rate}
              </span>
            </li>
          </ul>
        );
      case "GBP":
        return (
          <ul className="list-group">
            <li className="list-group-item">
              Bitcoin rate for{" "}
              <span className="badge badge-primary">
                {props.bpi.bpi.GBP.description}
              </span>{" "}
              is:&nbsp;
              <span className="badge badge-warning">
                {props.bpi.bpi.GBP.rate}
              </span>
            </li>
          </ul>
        );
    }
  };

  const selectPriceType = () => {
    return (
      <ul className={styles.buttonContainer}>
        <button
          className="btn btn-primary mr-2"
          onClick={() => setCurrency("USD")}
        >
          USD
        </button>
        <button
          className="btn btn-warning mr-2"
          onClick={() => setCurrency("EUR")}
        >
          EUR
        </button>
        <button className="btn btn-danger" onClick={() => setCurrency("GBP")}>
          GBP
        </button>
      </ul>
    );
  };

  return (
    <div>
      {selectPriceType()}
      {renderPrices()}
    </div>
  );
};

export default Prices;
